package org.nexage.sourcekit.util;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.graphics.Color;
import android.util.Base64;

public class Assets {
	public static final int mainAssetsColor = Color.parseColor("#B4FFFFFF");
	public static final int shadowColor = Color.parseColor("#5c000000");
	public static final int backgroundColor = Color.parseColor("#52000000");

	public static final String repeat = "";

	public static final String close = "";

	public static final String unmute = "";

	public static final String mute = "";

	public static Bitmap getBitmapFromBase64(String encodedString) {
		byte[] decodedString = Base64.decode(encodedString, Base64.DEFAULT);
		return BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
	}
}
